import{_ as i,k as e,a6 as r,j as l}from"./chunks/framework.CElylBbP.js";const u=JSON.parse('{"title":"Психологические защиты","description":"Психоаналитическая диагностика Нэнси МакВильямс","frontmatter":{"title":"Психологические защиты","description":"Психоаналитическая диагностика Нэнси МакВильямс","tags":"cards","cards":true,"wiki":"https://en.wikipedia.org/wiki/Defence_mechanism","list":"defenses"},"headers":[],"relativePath":"cards/defense/index.md","filePath":"cards/defense/index.md"}'),d={name:"cards/defense/index.md"};function p(h,a,t,o,s,n){return l(),e("div",null,a[0]||(a[0]=[r('<p><a href="/books/defenses.pdf" download="Психология-защиты-личности.pdf">Л.Ю. Субботина, М.В. Юркова: Психология защиты личности. Учебное пособие - скачать</a></p><h3 id="карточки-для-отработки-темы-в-группе" tabindex="-1"><a href="./game/">Карточки для отработки темы в группе</a> <a class="header-anchor" href="#карточки-для-отработки-темы-в-группе" aria-label="Permalink to &quot;[Карточки для отработки темы в группе](./game/)&quot;">​</a></h3><div class="card"><h2 id="задачи-защитного-поведения" tabindex="-1">Задачи защитного поведения <a class="header-anchor" href="#задачи-защитного-поведения" aria-label="Permalink to &quot;Задачи защитного поведения&quot;">​</a></h2><ul><li>избежать или овладеть неким мощным угрожающим чувством – тревогой, иногда сильнейшим горем или другими дезорганизующими эмоциональными переживаниями</li><li>сохранить самоуважение</li></ul><p>По мнению Нэнси Мак-Вильямс, «человек, применяющий защиту, обычно пытается бессознательно реализовать одну (или обе) из двух функций. В первом случае избежать или справиться с мощными угрожающими чувствами (тревога, глубокое горе, стыд, зависть) или другими дезорганизующими эмоциональными переживаниями. Во втором случае поддержать самооценку» (Мак-Вильямс, 2015). Она также отмечает, что у человека есть предпочитаемые защиты. «Стиль копинга (механизм для преодоления стресса) интегрирован. И он связан: с процессом научения (опытом переживания последствий использования конкретных защит); природой детских стрессовых факторов; защит, смоделированных значимыми взрослыми; конституциональными особенностями» (Мак-Вильямс, 2015).</p><p>В психоаналитическую концепцию механизмов психологических защит Мак-Вильямс вписывается психоаналитическая теория токсичности. Токсичность – бессознательная чрезмерная реакция психологических защит одного человека при столкновении с механизмами защит другого.</p></div><div class="card"><h2 id="индивидуальныи-набор-защит" tabindex="-1">Индивидуальный набор защит <a class="header-anchor" href="#индивидуальныи-набор-защит" aria-label="Permalink to &quot;Индивидуальный набор защит&quot;">​</a></h2><p>Предпочтительное автоматическое использование определенной защиты или набора защит является результатом сложного взаимодействия по меньшей мере четырех факторов:</p><ol><li>врожденного темперамента;</li><li>природы стрессов, пережитых в раннем детстве;</li><li>защит, образцами для которых (а иногда и сознательными учителями) были родители или другие значимые фигуры;</li><li>усвоенных опытным путем последствий использования отдельных защит (на языке теории обучения – эффект подкрепления).</li></ol></div><div class="card"><h2 id="две-категории-защит" tabindex="-1">Две категории защит <a class="header-anchor" href="#две-категории-защит" aria-label="Permalink to &quot;Две категории защит&quot;">​</a></h2><ol><li><a href="#первичные-защиты">Первичные</a>, примитивные, незрелые, «низшего порядка» — имеют дело с границей между собственным “я” и внешним миром. <ul><li>Действуют общим, недифференцированным образом во всем сенсорном пространстве индивида, сплавляя между собой когнитивные, аффективные и поведенческие параметры.</li></ul></li><li><a href="#вторичные-защиты">Вторичные</a>, развитые, зрелые, «высшего порядка» — «работают» с внутренними границами – между Эго, супер-Эго и Ид или между наблюдающей и переживающей частями Эго. <ul><li>Осуществляют определенные трансформации чего-то одного – мыслей, чувств, ощущений, поведения или некоторой их комбинации.</li></ul></li></ol></div><div class="card"><ul><li><a href="#задачи-защитного-поведения">Задачи защитного поведения</a></li><li><a href="#индивидуальный-набор-защит">Индивидуальный набор защит</a></li><li><a href="#две-категории-защит">Две категории защит</a></li><li><a href="#первичные-защиты">Первичные защиты</a><ul><li><a href="#изоляция">Изоляция</a></li><li><a href="#отрицание">Отрицание</a></li><li><a href="#всемогущий-контроль">Всемогущий контроль</a></li><li><a href="#идеализация">Идеализация</a></li><li><a href="#проекция">Проекция</a></li><li><a href="#интроекция">Интроекция</a></li><li><a href="#проективная-идентификация">Проективная идентификация</a></li><li><a href="#расщепление-эго">Расщепление Эго</a></li><li><a href="#диссоциация">Диссоциация</a></li><li><a href="#соматизация">Соматизация</a></li></ul></li><li><a href="#вторичные-защиты">Вторичные защиты</a><ul><li><a href="#репрессия-вытеснение">Репрессия (вытеснение)</a></li><li><a href="#регрессия">Регрессия</a></li><li><a href="#изоляция-чувства-от-понимания">Изоляция чувства от понимания</a></li><li><a href="#компенсация">Компенсация</a></li><li><a href="#аннулирование-возмещение">Аннулирование (возмещение)</a></li><li><a href="#игнорирование">Игнорирование</a><ul><li><a href="#отличия-от-других-защитных-механизмов">Отличия от других защитных механизмов</a></li></ul></li><li><a href="#интеллектуализация">Интеллектуализация</a></li><li><a href="#рационализация">Рационализация</a></li><li><a href="#морализация">Морализация</a></li><li><a href="#компартментализация-раздельное-мышление">Компартментализация (раздельное мышление)</a></li><li><a href="#поворот-против-себя">Поворот против себя</a></li><li><a href="#смещение">Смещение</a></li><li><a href="#реактивное-образование">Реактивное образование</a></li><li><a href="#реверсия">Реверсия</a></li><li><a href="#идентификация">Идентификация</a></li><li><a href="#отреагирование">Отреагирование</a></li><li><a href="#сексуализация-инстинктуализация">Сексуализация (инстинктуализация)</a></li><li><a href="#сублимация">Сублимация</a></li></ul></li></ul></div><div class="card"><h2 id="первичные-защиты" tabindex="-1">Первичные защиты <a class="header-anchor" href="#первичные-защиты" aria-label="Permalink to &quot;Первичные защиты&quot;">​</a></h2><p>Чтобы быть классифицированной как примитивная, защита должна обнаруживать наличие в себе двух качеств, связанных с довербальной стадией развития. Она должна иметь недостаточную связь с принципом реальности и недостаточный учет отделенности и константности объектов, находящихся вне собственного “Я”.</p></div><div class="card"><h3 id="изоляция" tabindex="-1">Изоляция <a class="header-anchor" href="#изоляция" aria-label="Permalink to &quot;Изоляция&quot;">​</a></h3><p>Тип личности: <strong>шизоидный</strong></p><p>Изолирование от социальных или межличностных ситуаций и замещение напряжения, происходящего от взаимодействий с другими, стимуляцией, исходящей от фантазий их внутреннего мира. Склонность к использованию химических веществ для изменения состояния сознания также может рассматриваться как разновидность изоляции. Более всего склонны к изоляции наиболее чувствительные дети.</p><p>У конституционально впечатлительных людей нередко развивается богатая внутренняя фантазийная жизнь, а внешний мир они воспринимают как проблематичный или эмоционально бедный. Склонность к изоляции может усиливаться вследствие эмоционального вторжения или столкновения с людьми, заботившимися о младенце, а также с другими ранними объектами. Человека, привычно изолирующегося и исключающего другие пути реагирования на тревогу, аналитики описывают как шизоидного.</p><p>Очевидный недостаток защиты изоляцией состоит в том, что она выключает человека из активного участия в решении межличностных проблем. Люди, имеющие дело с шизоидной личностью, зачастую не знают, как получить от нее какую-либо эмоциональную реакцию. Личности, постоянно укрывающиеся в собственном внутреннем мире, испытывают терпение тех, кто их любит, сопротивляясь общению на эмоциональном уровне.</p><p>Главное достоинство изоляции как защитной стратегии состоит в том, что, позволяя психологическое бегство от реальности, она почти не требует ее искажения. Человек, полагающийся на изоляцию, находит успокоение не в непонимании мира, а в удалении от него. Несмотря на отсутствие склонности к выражению собственных чувств, такой человек бывает очень восприимчив к чувствам других. На здоровом конце шизоидной оси мы находим людей, выдающихся своей креативностью: художников, писателей, ученых-теоретиков, философов, религиозных мистиков и других высокоталантливых созерцателей жизни, чье свойство находиться в стороне от стереотипов дает им способности к уникальному неординарному видению.</p></div><div class="card"><h3 id="отрицание" tabindex="-1">Отрицание <a class="header-anchor" href="#отрицание" aria-label="Permalink to &quot;Отрицание&quot;">​</a></h3><p><strong>Отказ принять существование неприятности.</strong></p><p>Тип личности: <strong>маниакальный</strong></p><p>Распротраненная автоматическая реакция на любую катастрофу. Это отзвук архаического процесса, уходящего корнями в детский эгоцентризм, когда познанием управляет дологическая убежденность: “Если я не признаю этого, значит, это не случилось”.</p><p>Человек, для которого отрицание является фундаментальной защитой, всегда настаивает на том, что “все прекрасно и все к лучшему”. Переживания восторга и всепоглощающей радости, особенно когда они возникают в ситуациях, в которых большинство людей нашли бы негативные стороны, также говорят о действии отрицания.</p><p>Большинство из нас до некоторой степени прибегает к отрицанию, с достойной целью сделать жизнь менее неприятной, и у многих людей есть свои конкретные области, где эта защита преобладает над остальными. Большинство людей, чьи чувства уязвлены, в ситуации, когда плакать неуместно или неразумно, более охотно откажутся от своих чувств, чем, полностью их осознавая, подавят слезы сознательным усилием. В чрезвычайных обстоятельствах способность к отрицанию опасности для жизни на уровне эмоций может оказаться спасительной. Благодаря отрицанию мы можем реалистически предпринять самые эффективные и даже героические действия. Каждая война оставляет нам массу историй о людях, которые “не потеряли головы” в ужасных, смертельно опасных обстоятельствах и в результате спасли себя и своих товарищей.</p><p>Самый очевидный пример психопатологии, обусловленной использованием отрицания, – мания. Пребывая в маниакальном состоянии, люди могут в невероятной степени отрицать свои физические потребности, потребность в сне, финансовые затруднения, личные слабости и даже свою смертность. В то время как депрессия делает совершенно невозможным игнорирование болезненных фактов жизни, мания придает им психологическую незначимость. Люди, для которых отрицание служит основной защитой, маниакальны по своему характеру.</p><p>Аналитики рассматривают перепады их настроения как результат периодических использований отрицания, за которыми каждый раз следует неизбежный “обвал”, когда у человека вследствие маниакального состояния наступает истощение.</p><p>Слегка гипоманиакальные люди могут быть очаровательны. Многие комические и эстрадные артисты демонстрируют остроумие, энергетический подъем, склонность играть словами и заразительно приподнятое настроение. Именно эти признаки характеризует людей, которые в течение длительного времени успешно отстраняют и трансформируют болезненные переживания. Но близкие и друзья нередко замечают и оборотную сторону их характера – тяжелую и депрессивную, и часто нетрудно увидеть психологическую цену их маниакального шарма.</p></div><div class="card"><h3 id="всемогущии-контроль" tabindex="-1">Всемогущий контроль <a class="header-anchor" href="#всемогущии-контроль" aria-label="Permalink to &quot;Всемогущий контроль&quot;">​</a></h3><p><strong>Потребность испытывать чувство всемогущественного контроля и интерпретировать происходящее как обусловленное собственной неограниченной властью</strong></p><p>Тип личности: <strong>психопатический</strong></p><p>Всемогу́щий контро́ль — психический процесс, относимый к механизмам психологической защиты. Заключается в бессознательной убеждённости человека в том, что он способен всё контролировать[1]. Естественным следствием такой убеждённости является ощущение человеком ответственности за всё вокруг и чувство вины, возникающее, если что-либо выходит из-под его контроля.</p><p>Ощущение, что ты способен влиять на мир, обладаешь силой, является, несомненно, необходимым условием самоуважения, берущего начало в инфантильных и нереалистических, однако на определенной стадии развития нормальных фантазиях всемогущества.</p><p>На инфантильной стадии первичного всемогущества, или грандиозности, фантазия обладания контролем над миром нормальна. По мере взросления ребенка она на следующей стадии естественным образом трансформируется в идею вторичного, “зависимого” или “производного” всемогущества, когда один из тех, кто первоначально заботится о ребенке, воспринимается как всемогущий.</p><p>В конце концов, по мере дальнейшего взросления, ребенок примиряется с тем неприятным фактом, что ни один человек не обладает неограниченными возможностями.</p><p>Некоторый здоровый остаток этого инфантильного ощущения всемогущества сохраняется во всех нас и поддерживает чувство компетентности и жизненной эффективности. Если мы эффективно осуществляем свое намерение, у нас возникает естественное “пиковое чувство”. Всякий, испытавший когда-либо ощущение “близкой удачи” и вслед за ним выигрыш в некоей азартной игре, знает, сколь прекрасно это чувство всемогущественного контроля.</p><p>Если личность организуется вокруг поиска и переживания удовольствия от ощущения, что она может эффективно проявлять и использовать собственное всемогущество, в связи с чем все этические и практические соображения отходят на второй план, существуют основания рассматривать эту личность как <strong>психопатическую</strong> (“социопатическая” и “антисоциальная” – синонимы более позднего происхождения).</p><p>“Перешагивать через” других – вот основное занятие и источник удовольствия для индивидов, в личности которых преобладает всемогущественный контроль. Их часто можно встретить там, где необходимы хитрость, любовь к возбуждению, опасность и готовность подчинить все интересы главной цели – проявить свое влияние. Эти люди появляются, например, на ключевых позициях в бизнесе, где требуется рисковать: в политической системе, армии, ЦРУ и в других организациях скрытого воздействия (в коммерции, среди вождей культов и лидеров евангелизма, в рекламной и развлекательной индустриях и во всех сферах, где много власти в чистом виде).</p></div><div class="card"><h3 id="идеализация" tabindex="-1">Идеализация <a class="header-anchor" href="#идеализация" aria-label="Permalink to &quot;Идеализация&quot;">​</a></h3><p>Тип личности: <strong>нарциссичный</strong></p><p>Постепенное замещение примитивных фантазий собственного всемогущества примитивными фантазиями о всемогуществе заботящегося лица. Одним из способов, которым ребенок может уберечь себя от этих подавляющих страхов, является вера в то, что кто-то , какая-то благодетельная всемогущая сила обеспечивает защиту.</p><p>Убежденность маленьких детей в том, что их мать или отец способны к сверхчеловеческим деяниям, – великое благо и вместе с тем бедствие родительства. Все мы склонны к идеализации. Мы несем в себе остатки потребности приписывать особые достоинства и власть людям, от которых эмоционально зависим.</p><p>Нормальная идеализация является существенным компонентом зрелой любви (Bergmann, 1987). И появляющаяся в ходе развития тенденция деидеализировать или обесценивать тех, к кому мы питали детскую привязанность, представляется нормальной и важной частью процесса сепарации-индивидуации.</p><p>У некоторых людей, однако, потребность идеализировать остается более или менее неизменной еще с младенчества. Их поведение обнаруживает признаки архаических отчаянных усилий противопоставить внутреннему паническому ужасу уверенность в том, что кто-то, к кому они привязаны, всемогущ, всеведущ и бесконечно благосклонен, и психологическое слияние с этим сверхъестественным Другим обеспечивает им безопасность. Они также надеются освободиться от стыда: побочным продуктом идеализации и связанной с ней веры в совершенство является то, что собственные несовершенства переносятся особенно болезненно; слияние с идеализируемым объектом – естественное в этой ситуации лекарство.</p><p>Томление по всемогущественному заботящемуся существу естественным образом выражается в религиозных верованиях. Более проблематичным оно предстает в таких феноменах, как настаивание на том, что собственный любовник совершенен, гуру непогрешим, школа самая лучшая, вкус безупречен, правительство неспособно ошибаться и тому подобных иллюзиях.</p><p>Самооценка у людей, личность которых построена на примитивной идеализации, искажается идеей, что любить себя самого можно лишь совершенствуясь.</p><p>Примитивное обесценивание – неизбежная оборотная сторона потребности в идеализации. Поскольку в человеческой жизни нет ничего совершенного, архаические пути идеализации неизбежно приводят к разочарованию. Чем сильнее идеализируется объект, тем более радикальное обесценивание его ожидает; чем больше иллюзий, тем тяжелее переживание их крушения.</p></div><div class="card"><h3 id="проекция" tabindex="-1">Проекция <a class="header-anchor" href="#проекция" aria-label="Permalink to &quot;Проекция&quot;">​</a></h3><p>тип личности: <strong>параноидный</strong></p><p>Проекция – это процесс, в результате которого внутреннее ошибочно воспринимается как приходящее извне. В своих благоприятных и зрелых формах она служит основой эмпатии. Поскольку никто не в состоянии проникнуть в чужую психику, для понимания субъективного мира другого человека мы должны опираться на способность проецировать собственный опыт. Интуиция, явления невербального синхронизма и интенсивные переживания мистического единства с другим человеком или группой связаны с проекцией собственного “Я”, при мощной эмоциональной отдаче для обеих сторон.</p><p>В тех случаях, когда спроецированные позиции серьезно искажают объект или когда спроецированное содержание состоит из отрицаемых и резко негативных частей собственного “Я”, возникают всевозможные проблемы.</p></div><div class="card"><h3 id="интроекция" tabindex="-1">Интроекция <a class="header-anchor" href="#интроекция" aria-label="Permalink to &quot;Интроекция&quot;">​</a></h3><p>Тип личности: <strong>депрессивный</strong></p><p>Интроекция — это процесс, в результате которого идущее извне ошибочно воспринимается как приходящее изнутри. В своих благоприятных формах она ведет к примитивной идентификации со значимыми другими. Маленькие дети вбирают в себя всевозможные позиции, аффекты и формы поведения значимых в их жизни людей.</p><p>В своих не столь позитивных формах интроекция, как и проекция, представляет собой очень деструктивный процесс. Наиболее известные и впечатляющие примеры патологической интроекции включают в себя процесс, названный, если учитывать его примитивность, несколько неудачно – “идентификация с агрессором”.</p><p>Когда кого-то мы любим или глубоко к кому-то привязаны, мы интроецируем этого человека, и его репрезентация внутри нас становится частью нашей идентичности (“Я сын Тома, муж Мэри, отец Сью, друг Дана” и так далее). Если человек, образ которого мы интернализовали, умер, разлучен с нами или отвержен, мы чувствуем не только, что окружающий нас мир стал беднее, но также что мы сами как-то уменьшились, какая-то часть нашего собственного “Я” умерла. Чувство пустоты начинает доминировать в нашем внутреннем мире.</p><p>Кроме того, если, стремясь воссоздать присутствие любимого объекта, вместо того, чтобы его отпустить, мы становимся поглощены вопросом о том, в результате какой нашей ошибки или греха он ушел от нас. Притягательная сила этого обычно неосознаваемого процесса основана на скрытой в нем надежде, что, поняв свою ошибку, мы вернем человека (еще одна манифестация инфантильного всемогущества). Таким образом, если мы пытаемся избежать горя, то взамен получаем бессознательные самоупреки.</p></div><div class="card"><h3 id="проективная-идентификация" tabindex="-1">Проективная идентификация <a class="header-anchor" href="#проективная-идентификация" aria-label="Permalink to &quot;Проективная идентификация&quot;">​</a></h3><blockquote><p>“При проективной идентификации не только пациент воспринимает терапевта искаженным образом, обусловленным ранними объектными отношениями пациента: кроме этого, на терапевта оказывается давление, чтобы он тоже переживал себя в соответствии с бессознательной фантазией пациента”. Огделл, 1982</p></blockquote><p>Иными словами, пациент не только проецирует внутренние объекты, но и вынуждает человека, на которого он их проецирует, вести себя подобно этим объектам – как если бы у него были те же самые интроекты.</p><p>И проекция, и интроекция имеют целый континуум форм – от самых примитивных до самых зрелых (Kernberg, 1976). На примитивном конце спектра они слиты, поскольку в них смешано внутреннее и внешнее. Это слияние мы и называем проективной идентификацией.</p><p>Этот процесс может проявляться в нашей обыденной жизни множеством тонких и вполне благотворных действий, без какой-либо психопатологии. Например, когда проецируемое и интроецируемое содержание вызывает чувства любви и радости, это может объединить группу благотворной эмоцией. Даже если это содержание негативно, но сам процесс не обладает качествами неумолимости, интенсивности и незатронутости со стороны других межличностных процессов более зрелого уровня, он совершенно не обязательно приводит к пагубным результатам.</p></div><div class="card"><h3 id="расщепление-эго" tabindex="-1">Расщепление Эго <a class="header-anchor" href="#расщепление-эго" aria-label="Permalink to &quot;Расщепление Эго&quot;">​</a></h3><p>Истоки его, как считается, находятся в довербальном периоде, когда младенец еще не может отдавать себе отчет в том, что заботящиеся о нем люди обладают и хорошими, и плохими качествами, и с ними связаны как хорошие, так и плохие переживания.</p><p>Пока нет константности объекта, не может быть и амбивалентности, поскольку амбивалентность предполагает наличие противоположных чувств к постоянному объекту. Вместо этого существует хорошее или плохое отношение к внешнему объекту.</p><p>В клинике мы наблюдаем расщепление, когда пациент занимает неамбивалентную позицию и воспринимает ее противоположность (то, что большинство из нас воспринимало бы амбивалентным) как нечто совершенно отдельное.</p></div><div class="card"><h3 id="диссоциация" tabindex="-1">Диссоциация <a class="header-anchor" href="#диссоциация" aria-label="Permalink to &quot;Диссоциация&quot;">​</a></h3><p>Диссоциация – это “нормальная” реакция на травму, но нельзя сказать, что в ходе развития обязательно должны быть травмы. Любой из нас, столкнувшись с катастрофой, большей, чем способен вынести (особенно если она связана с непереносимой болью или ужасом) может диссоциировать. Об отделении от тела во время угрожающих жизни бедствий и серьезных хирургических операций сообщалось так много, что лишь очень скептически настроенные люди могут полностью игнорировать свидетельства существования диссоциативных феноменов.</p><p>Человек, с которым произошло непереносимое несчастье, в любом возрасте может диссоциировать; маленькие дети, неоднократно подвергавшиеся ужасному абьюзу, могут научиться диссоциации как привычной реакции на стресс. В этом случае, если дети выживают и становятся взрослыми, они могут быть диагностированы как страдающие от характерологического диссоциативного расстройства и названы множественной личностью.</p><p>Выгоды диссоциирования в невыносимой ситуации очевидны: диссоциирующий отключается от страдания, страха, паники и уверенности в надвигающейся смерти. Всякий, кто пережил выход из тела, находясь в смертельной опасности, и даже тот, кто не имеет такой мощной основы для эмпатии, легко поймет, что лучше быть вне чувства ожидания предстоящего собственного уничтожения, чем внутри его. Эпизодическая или мягкая диссоциация может способствовать проявлениям редкого мужества. Огромным недостатком такой защиты является, конечно, ее тенденция автоматически включаться в условиях, когда на самом деле не существует риска для жизни, и более точная адаптация к реальной угрозе нанесла бы значительно меньший урон общему функционированию.</p><p>Травмированные люди склонны реагировать на обычный стресс как на опасность для жизни, немедленно впадая в амнезию или становясь совершенно другими – ко всеобщему смятению.</p></div><div class="card"><h3 id="соматизация" tabindex="-1">Соматизация <a class="header-anchor" href="#соматизация" aria-label="Permalink to &quot;Соматизация&quot;">​</a></h3><p>С момента введения термина его определение постепенно изменяется, так в коллективной монографии «Психосоматическая медицина» под редакцией Туре фон Икскюль соматизация описывается как нарушения функционирования той или иной системы без достаточного органического основания, но при важной роли психологических факторов, а Збигнев Липовский (англ.)рус. определил соматизацию как склонность переживать психологический стресс на физиологическом уровне.</p><p>Другое название этой формы защиты от регрессии — образование телесных симптомов или «бегство в болезнь». Эта защита проявляется в повышенном внимании к собственному здоровью и самочувствию. Такие люди могут с упоением говорить о своих болезнях, здоровом образе жизни, диетах и т. д. В своих взглядах на эти вещи они непреклонны, могут спорить, настаивать на своем мнении, слишком чувствительно реагировать на чужие замечания и советы в этих сферах.</p><p>Соматизация — это своего рода «отелеснивание» негативных эмоций, приводящее к возникновению дискомфортных телесных ощущений, а при их длительном существовании — разнообразных нарушений со стороны внутренних органов.</p></div><div class="card"><h2 id="вторичные-защиты" tabindex="-1">Вторичные защиты <a class="header-anchor" href="#вторичные-защиты" aria-label="Permalink to &quot;Вторичные защиты&quot;">​</a></h2><p>Нэнси Мак-Вильямс выбрала для описания “зрелые” защиты, или защиты “высшего порядка”, следуя двум критериям: (1) частота, с которой они упоминаются в психоаналитической клинической литературе и терапевтами-практиками; (2) их соотносимость с индивидуальными паттернами характера.</p></div><div class="card"><h3 id="репрессия-вытеснение" tabindex="-1">Репрессия (вытеснение) <a class="header-anchor" href="#репрессия-вытеснение" aria-label="Permalink to &quot;Репрессия (вытеснение)&quot;">​</a></h3><p>Тип личности: <strong>истерический</strong></p><p>Сутью репрессии является мотивированное забывание или игнорирование. Фрейд (Freud, 1915) писал, что “суть репрессии состоит в том, что нечто просто удаляется из сознания и удерживается на дистанции от него”. Если внутренний расклад или внешние обстоятельства достаточно огорчительны или способны привести пациента в замешательство, возможно их намеренное отправление в бессознательное. Этот процесс может применяться ко всему опыту, к аффекту, связанному с опытом, или к фантазиям и желаниям, ассоциированным с опытом.</p><p>Человек не способен вспомнить конкретные шокирующие, причиняющие боль жизненные события, но находится под давлением назойливых вспышек воспоминаний о них.</p><p>Современные аналитики считают, что человек должен достигнуть чувства целостности и непрерывности собственного “Я”, прежде чем станет способен сдерживать беспокоящие его импульсы репрессиией. У людей, ранний опыт которых не позволил им приобрести эту константность идентичности, неприятные чувства имеют тенденцию сдерживаться при помощи более примитивных защит – отрицание, проекция и расщепление (Myerson, 1991).</p><p>Как и другие бессознательные защиты, репрессия начинает создавать проблемы только тогда, когда она:</p><ol><li>не справляется со своей функцией (например, надежно удерживать беспокоящие мысли вне сознательного так, чтобы человек мог заниматься делом, приспосабливаясь к реальности);</li><li>стоит на пути определенных позитивных аспектов жизни;</li><li>действует при исключении других, более удачных способов преодоления трудностей.</li></ol><p>Элемент репрессии присутствует в действии большинства защит высшего порядка</p></div><div class="card"><h3 id="регрессия" tabindex="-1">Регрессия <a class="header-anchor" href="#регрессия" aria-label="Permalink to &quot;Регрессия&quot;">​</a></h3><p>Тип личности: <strong>инфантильный</strong></p><p>Социальное и эмоциональное развитие никогда не идет строго прямым путем; в процессе роста личности наблюдаются колебания, которые с возрастом становятся менее драматичными, но никогда полностью не проходят. Практически каждый человек, находясь в состоянии сильной усталости, начинает хныкать. Это возвращение к знакомому способу действия после того, как был достигнут новый уровень компетентности.</p><p>Некоторые ипохондричные люди, отвлекающие врачей монотонными неясными причитаниями и периодическими меняющимися жалобами, которые никогда не поддаются лечению, используют регрессию для того, чтобы находиться в роли слабого – самый ранний способ преодоления сложных жизненных аспектов. К тому времени, когда они должны проконсультироваться у терапевта, пациенты уже выстроили дополнительную и фактически непроницаемую стену защит, берущую начало в обращении с ними как с избалованными детьми или своенравными людьми, ищущими постоянного внимания.</p><p>Широко известно, что соматизация и ипохондрия, как и другие виды регрессии, являющие собой беспомощность и детские модели поведения, могут служить краеугольным камнем в характере личности.</p></div><div class="card"><h3 id="изоляция-чувства-от-понимания" tabindex="-1">Изоляция чувства от понимания <a class="header-anchor" href="#изоляция-чувства-от-понимания" aria-label="Permalink to &quot;Изоляция чувства от понимания&quot;">​</a></h3><p>Тип личности: <strong>обсессивная</strong></p><p>Одним из способов преодоления страха и других болезненных психических состояний является изоляция чувства от понимания. Более технически: аффективный аспект переживания или идеи может быть отделен от своей когнитивной составляющей.</p><p>Будучи очень важной в плане адаптации в экстремальных ситуациях, изоляция является в большей степени дискриминативной, чем диссоциация: из сознания удаляется не весь опыт переживания, а только его эмоциональное значение.</p><p>Изоляция может стать центральной защитой и при отсутствии травмы – в результате взаимного наложения определенного стиля воспитания и индивидуального темперамента ребенка. Все мы знаем людей, которые заявляют, что у них нет никакого эмоционального ответа на вещи, которые у большинства из нас вызывают очень сильные чувства. Такие люди иногда провозглашают изоляцию добродетелью и идеализируют состояние, выражающее только рациональный интерес.</p></div><div class="card"><h3 id="компенсация" tabindex="-1">Компенсация <a class="header-anchor" href="#компенсация" aria-label="Permalink to &quot;Компенсация&quot;">​</a></h3><p>Компенса́ция — защитный механизм психики, заключающийся в бессознательной попытке преодоления реальных и воображаемых недостатков. Термин введен Зигмундом Фрейдом, а позднее, понятый как жизненная стратегия, стал одним из центральных понятий в индивидуальной психологии Альфреда Адлера. Обычно компенсация проявляется в виде дополнительных усилий, прикладываемых к деятельности, как бы «компенсирующей» недостатки человека. Например, занятия спортом для инвалида будут являться компенсацией. Если усилия, затрачиваемые на компенсацию, оказываются неадекватно большими, то это уже называется <strong>ги́перкомпенсация</strong>.</p><p>Компенсаторное поведение может сильно различаться по форме. Так, оно может быть как социально приемлемым (слепой, становящийся музыкантом), так и нет (компенсация физической слабости злоупотреблением властными полномочиями); как прямым (попытка добиться успеха в той области, где ощущается недостаток), так и косвенным (стремление утвердить себя в другой сфере).</p></div><div class="card"><h3 id="аннулирование-возмещение" tabindex="-1">Аннулирование (возмещение) <a class="header-anchor" href="#аннулирование-возмещение" aria-label="Permalink to &quot;Аннулирование (возмещение)&quot;">​</a></h3><p>Тип личности: <strong>компульсивный</strong></p><p>Аннули́рование (возмеще́ние) — психологический процесс, относимый к механизмам психологической защиты, заключающийся в бессознательной попытке уравновесить некое чувство (обычно вину или стыд) с помощью отношения или поведения, которое «магическим образом» уничтожает это чувство[1].</p><p>Аннулирование может рассматриваться в качестве естественного преемника всемогущественного контроля. Аннулирование – термин, обозначающий бессознательную попытку уравновесить некоторый аффект (обычно вину или стыд) с помощью отношения или поведения, которые магическим образом уничтожают этот аффект.</p><p>Если мотив осознается, мы технически не можем называть это аннулированием. Но если аннулирующий не осознает чувства стыда или вины, и, следовательно, не может осознавать собственного желания искупить их, мы можем применять это понятие.</p><p>Многие религиозные ритуалы имеют аспект аннулирования. Попытки искупления грехов, даже совершенных только в мыслях, можно считать универсальным человеческим импульсом. Фантазии всемогущества отражаются в скрытом убеждении, проявляющемся в поведении, что враждебные мысли опасны, так как мысль равнозначна поступку.</p><p>Люди, которые испытывают сильные угрызения совести за предыдущие грехи, ошибки и провалы – реальные, преувеличенные или совершенные только в мыслях – могут планировать свою жизнь, используя аннулирование.</p><p>Аннулирование может быть одним из компонентов «бытовых ритуалов», направленных на снижение уровня тревоги. Когда потребность «возмещать ущерб» от собственных мыслей и чувств становится организующей всю личность потребностью, такую личность обычно классифицируют как компульсивную: её ритуалы становятся крайне навязчивыми и значимыми.</p></div><div class="card"><h3 id="игнорирование" tabindex="-1">Игнорирование <a class="header-anchor" href="#игнорирование" aria-label="Permalink to &quot;Игнорирование&quot;">​</a></h3><p>Игнори́рование (избега́ние, сленг — игно́р) — защитный механизм психики, заключающийся в бессознательном контроле информации об источнике неприятного воздействия, наличии или характере угрозы (опасности); ограничении количества такой информации или её искажённом восприятии.</p><h5 id="отличия-от-других-защитных-механизмов" tabindex="-1">Отличия от других защитных механизмов <a class="header-anchor" href="#отличия-от-других-защитных-механизмов" aria-label="Permalink to &quot;Отличия от других защитных механизмов&quot;">​</a></h5><p>Применяя эту защиту отдельно от других, человек не отказывается осознавать существование опасности или неприятного воздействия, как в случае отрицания, не забывает о ней, как в случае вытеснения, не перестаёт воспринимать окружающую реальность, как при примитивной изоляции и не принижает значимость информации, как при обесценивании. Он просто избегает ситуаций, в которых может получить неприятную ему информацию.</p></div><div class="card"><h3 id="интеллектуализация" tabindex="-1">Интеллектуализация <a class="header-anchor" href="#интеллектуализация" aria-label="Permalink to &quot;Интеллектуализация&quot;">​</a></h3><p>Интеллектуализацией называется вариант более высокого уровня изоляции аффекта от интеллекта. Человек, использующий изоляцию, обычно говорит, что не испытывает чувств, в то время как человек, использующий интеллектуализацию, разговаривает по поводу чувств, но таким образом, что у слушателя остается впечатление отсутствия эмоции.</p><p>Интеллектуализация сдерживает обычное переполнение эмоциями таким же образом, как изоляция сдерживает травматическую сверхстимуляцию. Когда человек может действовать рационально в ситуации, насыщенной эмоциональным значением, это свидетельствует о значительной силе Эго, и в данном случае защита действует эффективно. Многие люди чувствуют себя более зрело, когда интеллектуализируют в стрессовой ситуации, а не дают импульсивный, “сопляческий” ответ. Однако, если человек оказывается неспособным оставить защитную когнитивную неэмоциональную позицию (даже в таком провокационном случае, как с Дукакисом), то другие склонны интуитивно считать его эмоционально неискренним. Секс, добродушное поддразнивание, проявление артистизма и другие соответствующие взрослому человеку формы игры могут быть излишне ограничены у человека, который научился зависеть от интеллектуализации, справляясь с жизненными трудностями.</p></div><div class="card"><h3 id="рационализация" tabindex="-1">Рационализация <a class="header-anchor" href="#рационализация" aria-label="Permalink to &quot;Рационализация&quot;">​</a></h3><p>Рационализация может проявиться в любой из двух игр. Иногда нам не удается получить то, чего мы хотим, и тогда мы делаем вывод: не так уж и хотелось (иногда это явление называется термином “зеленый виноград” – по басне Эзопа о лисе и винограде). Или же происходит что-нибудь плохое. Тогда мы решаем: это не так уж и плохо (“сладкий лимон”).</p><p>Чем человек умнее и способнее к творчеству, тем лучшим рационализатором он является. Защита работает доброкачественно, если она позволяет человеку наилучшим образом выйти из трудной ситуации с минимумом разочарований. Однако как защита она имеет слабую сторону: фактически все может быть – и бывает – рационализировано. Люди редко делают что-либо только потому, что это хорошо для них. Они предпочитают обставить свои решения разумными доводами.</p></div><div class="card"><h3 id="морализация" tabindex="-1">Морализация <a class="header-anchor" href="#морализация" aria-label="Permalink to &quot;Морализация&quot;">​</a></h3><p>Тип личности: <strong>моральный мазохист</strong></p><p>Когда некто рационализирует, он бессознательно ищет приемлемые, с разумной точки зрения, оправдания для выбранного решения. Когда же он морализирует, это означает, что он ищет пути для того, чтобы чувствовать: он обязан следовать в данном направлении. Рационализация перекладывает то, что человек хочет, на язык разума, морализация направляет эти желания в область оправданий или моральных обязательств. Там, где рационализатор говорит “спасибо за науку” (что приводит к некоторому замешательству), морализатор будет настаивать на том, что это “формирует характер”.</p><p>Качество самооправдания, присущее такой специфической трансформации импульса, заставляет других считать ее забавной или смутно неприятной, хотя в определенных социальных и политических ситуациях лидеры, играющие на желании своих избирателей ощущать моральное превосходство, могут продуцировать массовое морализаторство абсолютно без услилий, и соблазненная подобным образом публика вряд ли это заметит.</p><p>Иногда морализацию можно рассматривать как более высокоразвитую версию расщепления. Хотя я и не встречала подобного ее представления в психоаналитической литературе, думается, что склонность к морализации будет поздней стадией примитивной тенденции глобального деления на плохое и хорошее. В то время как расщепление у ребенка естественным образом возникает прежде способности его интегрированного собственного “Я” выносить амбивалентность, решение в форме морализации через обращение к принципам смешивает чувства, которые развивающееся собственное “Я” способно выносить.</p></div><div class="card"><h3 id="компартментализация-раздельное-мышление" tabindex="-1">Компартментализация (раздельное мышление) <a class="header-anchor" href="#компартментализация-раздельное-мышление" aria-label="Permalink to &quot;Компартментализация (раздельное мышление)&quot;">​</a></h3><p>Его функция состоит в том, чтобы разрешить двум конфликтующим состояниям сосуществовать без осознанной запутанности, вины, стыда или тревоги. Тогда как изоляция подразумевает разрыв между мыслями и эмоциями, раздельное мышление означает разрыв между несовместимыми мысленными установками. Когда некто использует компартментализацию, он придерживается двух или более идей, отношений или форм поведения, конфликтующих друг с другом, без осознания этого противоречия. Для непсихологически думающего наблюдателя раздельное мышление ничем не отличается от лицемерия. При конфронтации человек, использующий раздельное мышление, будет рационализировать противоречия, чтобы избавиться от них.</p></div><div class="card"><h3 id="поворот-против-себя" tabindex="-1">Поворот против себя <a class="header-anchor" href="#поворот-против-себя" aria-label="Permalink to &quot;Поворот против себя&quot;">​</a></h3><p>Тип личности: <strong>депрессивный</strong></p><p>Это понятие означает то же, что предположил бы неспециалист, а именно – перенаправление негативного аффекта, относящегося к внешнему объекту, на себя.</p><p>Если некто критически настроен по отношению к авторитетному человеку, чье расположение кажется ему основой безопасности, и если он думает, что этот человек не сможет вынести критики, он будет чувствовать себя безопаснее, направив критические мысли и идеи вовнутрь. Детей, от которых не зависит выбор того, где им жить, и которые могут заплатить высокую цену за обиды, нанесенные заботливому и душевному воспитателю, защита в форме поворота против себя может отвлечь от намного более печального факта, что их благополучие зависит от независимого взрослого. Однако самокритику чувствовать неприятно, и тем более эмоционально предпочтительнее знать о реальной угрозе своему выживанию в условиях, когда человек не имеет власти изменить порядок вещей.</p><p>У большинства из нас существует тенденция обращать против себя негативные аффекты, отношения и восприятия благодаря иллюзии, что этот процесс дает нам больше контроля над неприятными ситуациями. Поворот против себя является популярной защитой среди более здоровых людей, которые устойчивы перед искушением отрицать или проецировать неприятные качества, а также у тех, у кого подобные тенденции вызывают тревогу. Они предпочитают заблуждаться, считая, что трудности – это скорее их вина, чем чья-то еще.</p></div><div class="card"><h3 id="смещение" tabindex="-1">Смещение <a class="header-anchor" href="#смещение" aria-label="Permalink to &quot;Смещение&quot;">​</a></h3><p>Термин “смещение” относится к перенаправлению драйва, эмоции, озабоченности чем-либо или поведения с первоначального или естественного объекта на другой, потому что его изначальная направленность по какой-то причине тревожно скрывается.</p><p>Некоторые печальные культурные тенденции – как расизм, сексизм, гетеросексизм, громкое обличение проблем общества группами, лишенными гражданских прав и имеющими слишком мало власти, чтобы отстоять свои права, содержат в себе значительный элемент смещения. Все это отражает тенденцию находить козла отпущения (что легко можно заметить в большинстве организаций и субкультур). Перенос как в клинических, так и во внеклинических проявлениях, которые Салливан назвал “паратаксическими искажениями”, содержит в себе смещение (чувств, направленных на объекты, важные в раннем детстве) наряду с проекцией (внутренних характерных особенностей собственного “Я”). Положительные виды смещения включают в себя перевод агрессивной энергии в созидательную активность (огромное количество домашней работы выполняется, когда люди находятся в возбужденном состоянии), а также переадресовку эротических импульсов с нереальных или запрещенных сексуальных объектов на доступного партнера.</p></div><div class="card"><h3 id="реактивное-образование" tabindex="-1">Реактивное образование <a class="header-anchor" href="#реактивное-образование" aria-label="Permalink to &quot;Реактивное образование&quot;">​</a></h3><p>Реактивное образование как защита является очень любопытным феноменом. Очевидно, что человеческий организм способен повернуть нечто совершенно в противоположную сторону для того, чтобы свести угрозу к минимуму. Традиционное определение реактивного формирования подразумевает преобразование негативного аффекта в позитивный или наоборот. Например, трансформация ненависти в любовь, привязанности в презрение, враждебности в дружелюбие содержит в себе много общих трансакций.</p><p>Реактивное образование является излюбленной защитой в тех случаях психопатологии, когда враждебные чувства и агрессивные импульсы являются главным содержанием, и на опыте проверено, насколько опасно не уметь держать их в руках. Например, параноидные личности часто испытывают только ненависть и подозрительность, когда сторонний наблюдатель полагает, что они также способны чувствовать привязанность и зависимость. Обсессивные и компульсивные пациенты нередко свято убеждены, что испытывают лишь уважение и признательность к авторитетам, тогда как другие считают, что они чувствуют и обиду.</p></div><div class="card"><h3 id="реверсия" tabindex="-1">Реверсия <a class="header-anchor" href="#реверсия" aria-label="Permalink to &quot;Реверсия&quot;">​</a></h3><p>Еще одним способом справиться с чувствами, которые представляют психологическую угрозу собственному “Я”, является проигрывание сценария, переключающего отношение человека с субъекта на объект или наоборот. Например, если некто чувствует, что желание испытывать заботу со стороны других является постыдным или содержит угрозу, он может жертвенно удовлетворить свою потребность в зависимости, проявляя заботу о другом и бессознательно идентифицируясь с этим человеком, получающим удовлетворение от заботы о себе. Этот частный случай реверсии является оправданным временем приспособлением терапевтов, часто испытывающих чувство дискомфорта от собственной зависимости, но которые бывают счастливы забоатиться о ком-то.</p><p>Как только ребенок достигает возраста, когда он начинает играть с куклами или “ролевыми персонажами” (куклы для мальчиков сейчас тоже продаются), о нем можно сказать: он использует реверсию. Достоинством реверсии является то обстоятельство, что человек перемещает сильные аспекты трансакций таким образом, чтобы играть скорее в инициирующую роль, чем отвечающую. Сторонники теории контроля-овладения называют это явление “трансформацией пассивного в активное”. Если развивается положительный сценарий, защита работает конструктивно. Если же имеет место отрицательный сценарий – деструктивно. Например, в общинах при унизительных и других обрядах посвящения, связанных с насилием, опыт преследуемой во время посвящения жертвы трансформируется, и ситуация начинает ощущаться как положительная благодаря переключению с пассивной роли на активную, с жертвы – на преследователя.</p></div><div class="card"><h3 id="идентификация" tabindex="-1">Идентификация <a class="header-anchor" href="#идентификация" aria-label="Permalink to &quot;Идентификация&quot;">​</a></h3><p>Включение идентификации в список защитных механизмов может показаться излишним, так как большинство из нас расценивают возможность идентифицироваться с другим человеком или с его отдельными сторонами как конструктивную незащитную тенденцию. Установлено, что только некоторые виды идентификации могут считаться компонентами защит (например, той, которую психологи с ориентацией на социальное научение назвали “моделирование”). Но психоаналитически думающие терапевты продолжают считать, что многие виды идентификации вызываются необходимостью избегать тревогу, горе, стыд, другие болезненные аффекты, или для того, чтобы поддерживать себя, ощущая угрозу чувству самоуважения и целостности. Как и другие зрелые защитные процессы, идентификация является нормальным аспектом психологического развития и становится проблематичной только в определенных условиях.</p><p>Фрейд (1923) был первым, кто предложил различать защитную и незащитную идентификацию —”анаклитическую” идентификацию (от греческого слова, означающего “полагаться на”) – и “идентификацию с агрессором”. Первый тип идентификации мотивируется невыполненным желанием походить на значимого человека (“Мамочка великодушна и создает комфорт, и я хочу быть как она”). Второй тип Фрейд рассматривал как автоматический, но мотивированный защитным решением проблемы ощущения угрозы со стороны другого человека, обладающего властью (“Я боюсь мамочкиного наказания за мои враждебные импульсы; если я стану как она, ее власть будет внутри меня, а не вне меня”). Фрейд полагал, что многие действия идентификации содержат элементы как непосредственного прямого принятия того, что любимо, так и защитного уподобления тому, что является пугающим.</p></div><div class="card"><h3 id="отреагирование" tabindex="-1">Отреагирование <a class="header-anchor" href="#отреагирование" aria-label="Permalink to &quot;Отреагирование&quot;">​</a></h3><p>Таким образом, термин отреагирование относится к любому виду поведения, которое предполагает выражение отношений переноса, привносить которые в терапию в словесной форме пациент чувствует для себя еще недостаточно безопасным. Этот термин может быть также применим к процессу, благодаря которому любое отношение вне или внутри терапии разряжается в действии с бессознательной целью справиться со страхами, связанными с этим отношением. То, что отреагируется вовне, преимущественно саморазрушительно или преимущественно способствует росту, или может быть в некоторой степени и тем, и другим. То, что заставляет отреагировать вовне, не является ни плохим ни хорошим, но такова бессознательная и пугающая природа импульсов, толкающая человека к действию компульсивным, автоматическим образом, который отличает поведение при отреагировании вовне. Использование популярного сегодня слова “отреагирование” для обозначения любого нежелательного поведения, например, детского шума или невежливого приветствия, является психоаналитически неоправданным. Негативный оттенок, который приобрело это выражение, нередко отражает тот факт, что позитивные виды отреагирования не привлекают к себе внимания так, как это свойственно его деструктивным видам.</p><p>Существует несколько навязчивых ярлыков, созданных аналитиками для обозначения обычно бессознательных типов поведения, которые подпадают под общий заголовок отреагирования. Сюда входят, например, эксгибиционизм, вуайеризм, садизм, мазохизм, перверсии и все понятия, содержащие приставку “контр”: контрфобия, контрзависимость, контрвраждебность и так далее*. Все эти тенденции, когда они относятся к действиям, понимаемым как защитные, дают основание предполагать лежащий в их основе страх или другие непризнанные негативные чувства. Раннее наблюдение Фрейда – мы отреагируем то, чего не помним – по-прежнему остается проницательным, особенно, если допустить, что причиной, по которой мы чего-то не помним, является нечто чрезвычайно болезненное, переходящее из состояния невозможности вспомнить в состояние отыгрывания в данный момент.</p></div><div class="card"><h3 id="сексуализация-инстинктуализация" tabindex="-1">Сексуализация (инстинктуализация) <a class="header-anchor" href="#сексуализация-инстинктуализация" aria-label="Permalink to &quot;Сексуализация (инстинктуализация)&quot;">​</a></h3><p>Объективно говоря, сексуальность является сильной динамической основой в человеческих существах, и человеческое сексуальное поведение во многих случаях можно приравнять к соответственно направленному выражению желания продолжения рода.</p><p>Люди могут сексуализировать любой опыт, бессознательно стремясь превратить ужас, боль или другое переполняющее чувство в восторг. В аналитической литературе этот процесс называется также инстинктуализацией . Сексуальное побуждение – наиболее действенный способ почувствовать, что ты жив. Детским страхом смерти, который испытал ребенок, оставшийся один, ужасом перенесенного насилия над ним или другого страшного несчастья можно управлять психологически посредством превращения травматической ситуации в жизнеутверждающую. Изучение людей с необычными сексуальными наклонностями часто открывало опыт детских переживаний, которые превосходили способность ребенка справляться с ними и вследствие этого были трансформированы в самоинициированную сексуализацию травмы. Например, в работе Столлера о сексуальных мазохистах (Stoller, 1975) обнаружилось, что многие из тех, кто говорил о необходимости испытывать боль для достижения наивысшего эротического наслаждения, перенесли внутренние болезненные вмешательства при лечении в детском возрасте.</p><p>В общем, многие из нас используют сексуализацию для того, чтобы преодолеть и сделать более приятными некоторые печальные события в нашей жизни. Для людей разного пола имеются различия в том, что они склонны сексуализировать: для женщин более характерно сексуализировать зависимость, а для мужчин – агрессивность. Некоторые люди сексуализируют деньги, другие – грязь, третьи – власть и так далее. Многие из нас сексуализируют процесс обучения; эротичность присутствия талантливого учителя была отмечена со времен Сократа. Тенденцией людей эротизировать свою реакцию на кого-либо, представляющего власть, можно объяснить тот факт, почему политики и другие избранники имеют так много сексуально доступных поклонников и почему возможность сексуального насилия и сексуальной эксплуатации так велика среди влиятельных и известных людей.</p></div><div class="card"><h3 id="сублимация" tabindex="-1">Сублимация <a class="header-anchor" href="#сублимация" aria-label="Permalink to &quot;Сублимация&quot;">​</a></h3><p>Сублимация была тем обозначением, которое первоначально Фрейд дал социально приемлемому выражению базирующихся на биологии импульсов (к которым относятся стремления сосать, кусать, есть, драться, совокупляться, разглядывать других и демонстрировать себя, наказывать, причинять боль, защищать потомство и так далее). Так, Фрейд указывал, что дантист может сублимировать садизм, выставляющийся художник – эксгибиционизм, адвокат – желание уничтожать врагов. Согласно Фрейду, инстинктивные желания обретают силу влияния, благодаря обстоятельствам детства индивида; некоторые драйвы или конфликты приобретают особое значение и могут быть направлены на полезную созидательную деятельность.</p><p>Данная защита расценивается как здоровое средство разрешения психологических трудностей по двум причинам: во-первых, она благоприятствует конструктивному поведению, полезному для группы, во-вторых, она разряжает импульс вместо того, чтобы тратить огромную эмоциональную энергию на трансформацию его во что-либо другое (например, как при реактивном формировании) или на противодействие ему противоположно направленной силой (отрицание, репрессия). Такая разрядка энергии считается положительной по своей сути: она позволяет человеческому организму поддерживать необходимый гомеостаз (Fenichel, 1945).</p></div>',36)]))}const f=i(d,[["render",p]]);export{u as __pageData,f as default};
